  }
  #anchored {
    position: absolute;
    position-anchor: --a;
    position-try-options: --pt flip-start;
    inset: 0;
    top: anchor(top);
    bottom: anchor(bottom);
    right: calc(anchor(left) + 5px);
    width: 50px;
    height: 50px;
    background: skyblue;
    justify-self: end;
  }
  #anchored.flip {
    background: seagreen;
    width: 300px; /* Force overflow */
  }
</style>
<div id=cb>
  <div id=anchor></div>
  <div id=anchored></div>
</div>
