<pre id=pre>
</pre>
<script>
function render() {
  let cs = getComputedStyle(anchored);
  pre.textContent = `
    justify-self: ${cs.justifySelf};
    align-self: ${cs.alignSelf};
  `;
}
render();

document.querySelector('button').addEventListener('click', () => {
  anchored.classList.toggle('flip');
  render();
});
</script>
